import matplotlib.pyplot as pltimport pandas as pdimport seaborn as sns  # import file with datadf = pd.read_csv("MRK.csv")df = df.drop(["Close_lead1"],axis = 1)# Increase the size of the heatmap.plt.figure(figsize=(40, 20))# Store heatmap object in a variable to easily access it when you want to include more features (such as title).# Set the range of values to be displayed on the colormap from -1 to 1, and set the annotation to True to display the correlation values on the heatmap.heatmap = sns.heatmap(df.corr(), vmin=-1, vmax=1, annot=True)# Give a title to the heatmap. Pad defines the distance of the title from the top of the heatmap.heatmap.set_title('Correlation Heatmap', fontdict={'fontsize':12}, pad=12);